import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';

const App = () => {
    const [data, setData] = useState([]);
    //console.log('data: ', data);
    //const intPrices = data.bpi
    const [intPrices, setIntPrices] = useState([])
    console.log(intPrices);


    const getPriceInfo = async () => {
      const resp = await fetch('https://api.coindesk.com/v1/bpi/currentprice.json');
      const priceInfo = await resp.json();
      const international = priceInfo.bpi
      setIntPrices(international)
      //console.log('priceInfo: ', priceInfo);
      setData(priceInfo);
    }

    useEffect(() => {
      getPriceInfo();
    }, []);

    return <div>
      {

        intPrices.map((country) => (
       <div className="country">{country.rate}</div>
        ))}

    </div>
  }

  ReactDOM.render(
    <App />,
    document.getElementById('app'),
  );
