          <div className="content">
            <h2>GBP</h2>
            <div className="usaCurrency">GBP/BTC</div>
            <div className="USDprice">£ {mrWorldwide.GBP.rate}</div>
          </div>
        </div>
        <div className="card">
          <div className="content">
            <h2>EUR</h2>
            <div className="usaCurrency">EUR/BTC</div>
            <div className="USDprice">€ {mrWorldwide.EUR.rate}</div>
          </div>
        </div>
      </div>

  </div>
    } 
    else{
    return <div>
        bitcoin price 
      { 

    }
  </div>
  }
}

  ReactDOM.render(
    <App />,
