      </div>
      <div className="GBP">{mrWorldwide.USD.code}
      <div className="USDprice">{mrWorldwide.symbol}{mrWorldwide.USD.rate}</div>
      </div>

  </div>
    } else{
    return <div>
        bitcoin price 
      { 
